mapper.select:
SELECT * FROM dish


        name LIKE CONCAT('%',#{name},'%')


        AND category_id = #{categoryId}


        AND status = #{status}


ORDER BY create_time DESC

SELECT d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.create_time, d.update_time, d.create_user, d.update_user, c.name AS categoryName FROM dish d LEFT JOIN category c ON d.category_id = c.id
d.name LIKE CONCAT('%', #{name}, '%')
AND d.category_id = #{categoryId}
AND d.status = #{status}
ORDER BY d.create_time DESC
