mapper.select:
SELECT s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.create_time, s.update_time, s.create_user, s.update_user, c.name AS categoryName FROM setmeal s LEFT JOIN category c ON s.category_id = c.id
s.name LIKE CONCAT('%', #{name}, '%')
AND s.category_id = #{categoryId}
AND s.status = #{status}
ORDER BY s.create_time DESC
