mapper.select:
select * from shopping_cart


        and user_id = #{userId}


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}


order by create_time desc

mapper.delete:
delete from shopping_cart

    user_id = #{userId}

        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}
